operation.add:
				/// Enhanced Options+
if ($this->config->get('module_enhanced_options_status')) {
	$this->option_ratio = TRUE;

	if (isset($this->config->get('module_enhanced_options')['text'][$this->config->get('config_language_id')])) {
		foreach ($this->config->get('module_enhanced_options')['text'][$this->config->get('config_language_id')] as $key => $value) {
			$this->{'text_' . $key} = $value;
		}
	}
} else {
	$this->option_ratio = FALSE;
}

				/// <<< Enhanced Options+
if ($is_free &&
	$this->config->get('module_enhanced_options_status') &&
	$this->config->get('module_enhanced_options')['notices'] &&
	$this->config->get('module_enhanced_options')['text'][$this->config->get('config_language_id')]['free']
) {
	$option_price = $this->config->get('module_enhanced_options')['text'][$this->config->get('config_language_id')]['free'];
}

if ($option_type === 'select') {
	if (!$this->option_hide_price && $option_price && $option_oldprice) {
		$product_option_value_data[] = array(
			'name'                    => $product_option_value['name'],
			'option_value_id'         => $product_option_value['option_value_id'],
			'points'                  => $option_points,
			'points_prefix'           => $points_prefix,
			'price'                   => $option_oldprice . ' - ' . $this->text_oldprice,
			'price_prefix'            => $price_prefix,
			'product_option_value_id' => $product_option_value['product_option_value_id'] . '-disabled',
		);

		$option_price = $option_price .  ' - ' . $this->text_newprice;
	}

} else {
	if ($this->option_hide_price)  {
		$option_price = '';
	} else {

		if ($option_oldprice && $option_price) {
			$option_price .= ' <span class="option-price-old" style="text-decoration:line-through;">' . $option_oldprice . '</span>';
		}
	}
}

if (!$option_subtract && $option_quantity == 0 && $option_price &&
	$this->config->get('module_enhanced_options_status') &&
	$this->config->get('module_enhanced_options')['notices'] &&
	$this->config->get('module_enhanced_options')['text'][$this->config->get('config_language_id')]['preorder']
) {
	$option_price .= $this->config->get('module_enhanced_options')['text'][$this->config->get('config_language_id')]['preorder'];
}
/// Enhanced Options+ >>>
